select bit_or_blob(prov_c1) as prov_c1, bit_or_blob(prov_o1) as prov_o1, bit_or_blob(prov_l1) as prov_l1 from (
select 	c_name,c_custkey,o_orderkey,o_orderdate,o_totalprice,sum(l_quantity), bit_or_ints(prov_c1) as prov_c1, bit_or_ints(prov_o1) as prov_o1, bit_or_ints(prov_l1) as prov_l1
from
(select c_acctbal, c_address, c_name, c_phone, c_comment, c_nationkey, c_custkey, binsearch_ranges_pos(c_custkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(45139),(92080),(139617),(184356),(235427),(280186),(329218),(377297),(423351),(471001),(517679),(565547),(610694),(656970),(705706),(751777),(797177),(842308),(889590),(937275),(981293),(1029789),(1076617),(1123335),(1171160),(1220816),(1264890),(1310610),(1357306),(1404538),(1451555),(1500001)) AS g(b))) AS prov_c1 from customer) c1,
(select l_quantity,l_returnflag,l_orderkey, l_shipdate, l_extendedprice, l_discount, binsearch_ranges_pos(l_orderkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(1942309),(3780867),(5680996),(7438596),(9296355),(11172832),(13073988),(14915840),(16607975),(18439395),(20304326),(22259780),(24101414),(26059811),(27936422),(29847456),(31642433),(33662309),(35482019),(37259143),(38977191),(40925344),(42899968),(44780128),(46500129),(48470790),(50521505),(52554116),(54495366),(56323682),(58154343),(60000001)) AS g(b))) AS prov_l1 from lineitem) l,
(select o_totalprice,o_shippriority,o_orderdate,o_orderkey,o_custkey, binsearch_ranges_pos(o_orderkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(1651200),(3614019),(5584739),(7361315),(9169893),(10921285),(12788353),(14710887),(16654374),(18647584),(20535014),(22383874),(24300448),(26196295),(28125798),(30112293),(31919138),(33737222),(35495300),(37142306),(39101826),(40951842),(42883267),(44809991),(46850850),(48828611),(50721988),(52488768),(54500966),(56382754),(58251399),(60000001)) AS g(b))) AS prov_o1 from orders) o1,
(select * from (select sum(l_quantity) as total, l_orderkey from lineitem group by l_orderkey) t where total > 312) ol
where o_orderkey = ol.l_orderkey and c_custkey = o_custkey and o_orderkey = l.l_orderkey group by c_name,c_custkey,o_orderkey,o_orderdate,o_totalprice order by o_totalprice desc,o_orderdate LIMIT 100) f;
