select bit_or_blob(prov_c1) as prov_c1, bit_or_blob(prov_o1) as prov_o1, bit_or_blob(prov_l1) as prov_l1 from (
select 	c_name,c_custkey,o_orderkey,o_orderdate,o_totalprice,sum(l_quantity), bit_or_ints(prov_c1) as prov_c1, bit_or_ints(prov_o1) as prov_o1, bit_or_ints(prov_l1) as prov_l1
from
(select c_acctbal, c_address, c_name, c_phone, c_comment, c_nationkey, c_custkey, binsearch_ranges_pos(c_custkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(4688),(9376),(14064),(18752),(23440),(28128),(32816),(37504),(42192),(46880),(51568),(56256),(60944),(65632),(70320),(75008),(79695),(84382),(89069),(93756),(98443),(103130),(107817),(112504),(117191),(121878),(126565),(131252),(135939),(140626),(145313),(150001)) AS g(b))) AS prov_c1 from customer) c1,
(select l_quantity,l_returnflag,l_orderkey, l_shipdate, l_extendedprice, l_discount, binsearch_ranges_pos(l_orderkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(186977),(375301),(562278),(749568),(937314),(1125348),(1312711),(1500738),(1687907),(1874917),(2062599),(2250438),(2438022),(2626021),(2813156),(3000961),(3188294),(3375589),(3562148),(3749957),(3937667),(4125286),(4312550),(4500354),(4687457),(4875014),(5062566),(5250567),(5437926),(5625987),(5813189),(6000001)) AS g(b))) AS prov_l1 from lineitem) l,
(select o_totalprice,o_shippriority,o_orderdate,o_orderkey,o_custkey, binsearch_ranges_pos(o_orderkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(187491),(374982),(562497),(749988),(937479),(1124994),(1312485),(1500000),(1687491),(1874982),(2062497),(2249988),(2437479),(2624994),(2812485),(3000000),(3187491),(3374982),(3562497),(3749988),(3937479),(4124994),(4312485),(4500000),(4687491),(4874982),(5062497),(5249988),(5437479),(5624994),(5812485),(6000001)) AS g(b))) AS prov_o1 from orders) o1,
(select * from (select sum(l_quantity) as total, l_orderkey from lineitem group by l_orderkey) t where total > 312) ol
where o_orderkey = ol.l_orderkey and c_custkey = o_custkey and o_orderkey = l.l_orderkey group by c_name,c_custkey,o_orderkey,o_orderdate,o_totalprice order by o_totalprice desc,o_orderdate LIMIT 100) f;
