select 	c_name,c_custkey,o_orderkey,o_orderdate,o_totalprice,sum(l_quantity) from customer, orders, lineitem l, (select * from (select sum(l_quantity) as total, l_orderkey from lineitem group by l_orderkey) t where total > 312) ol where o_orderkey = ol.l_orderkey and c_custkey = o_custkey and o_orderkey = l.l_orderkey group by c_name,c_custkey,o_orderkey,o_orderdate,o_totalprice order by o_totalprice desc,o_orderdate LIMIT 100;
