select s_acctbal,s_name,n_name,p_partkey,p_mfgr,s_address,s_phone,s_comment
from (select * from part where (("p_partkey" >= 1) AND ("p_partkey" < 21875)) OR (("p_partkey" >= 25000) AND ("p_partkey" < 40625)) OR (("p_partkey" >= 43750) AND ("p_partkey" < 50000)) OR (("p_partkey" >= 59375) AND ("p_partkey" < 62500)) OR (("p_partkey" >= 65625) AND ("p_partkey" < 90625)) OR (("p_partkey" >= 96875) AND ("p_partkey" < 112500)) OR (("p_partkey" >= 115625) AND ("p_partkey" < 131250)) OR (("p_partkey" >= 134375) AND ("p_partkey" < 146875)) OR (("p_partkey" >= 150000) AND ("p_partkey" < 159375)) OR (("p_partkey" >= 162500) AND ("p_partkey" < 200001))) pp, (select * from supplier where 
(("s_suppkey" >= 1) AND ("s_suppkey" < 156)) OR (("s_suppkey" >= 780) AND ("s_suppkey" < 1560)) OR (("s_suppkey" >= 1716) AND ("s_suppkey" < 1872)) OR (("s_suppkey" >= 2184) AND ("s_suppkey" < 2964)) OR (("s_suppkey" >= 3120) AND ("s_suppkey" < 3588)) OR (("s_suppkey" >= 3744) AND ("s_suppkey" < 5462)) OR (("s_suppkey" >= 5618) AND ("s_suppkey" < 6401)) OR (("s_suppkey" >= 6557) AND ("s_suppkey" < 6870)) OR (("s_suppkey" >= 7027) AND ("s_suppkey" < 7496)) OR (("s_suppkey" >= 7653) AND ("s_suppkey" < 7809)) OR (("s_suppkey" >= 7966) AND ("s_suppkey" < 8122)) OR (("s_suppkey" >= 8279) AND ("s_suppkey" < 8592)) OR (("s_suppkey" >= 8748) AND ("s_suppkey" < 9218)) OR (("s_suppkey" >= 9374) AND ("s_suppkey" < 10001))
) ss,(select * from partsupp where (("ps_partkey" >= 1) AND ("ps_partkey" < 21875)) OR (("ps_partkey" >= 25000) AND ("ps_partkey" < 40625)) OR (("ps_partkey" >= 43750) AND ("ps_partkey" < 50000)) OR (("ps_partkey" >= 59375) AND ("ps_partkey" < 62500)) OR (("ps_partkey" >= 65625) AND ("ps_partkey" < 90625)) OR (("ps_partkey" >= 96875) AND ("ps_partkey" < 112500)) OR (("ps_partkey" >= 115625) AND ("ps_partkey" < 131250)) OR (("ps_partkey" >= 134375) AND ("ps_partkey" < 146875)) OR (("ps_partkey" >= 150000) AND ("ps_partkey" < 159375)) OR (("ps_partkey" >= 162500) AND ("ps_partkey" < 200001))) ps,nation,region, (select min(ps_supplycost) as min_supplycost, ps_partkey from partsupp, supplier, nation, region where s_suppkey = ps_suppkey and s_nationkey = n_nationkey and n_regionkey = r_regionkey and r_name = 'EUROPE' group by ps_partkey) m
where p_partkey = ps.ps_partkey and s_suppkey = ps.ps_suppkey and p_size = 15 and p_type like '%BRASS' and s_nationkey = n_nationkey and n_regionkey = r_regionkey and r_name = 'EUROPE'
and ps_supplycost = m.min_supplycost and p_partkey = m.ps_partkey
order by s_acctbal desc, n_name, s_name, p_partkey
LIMIT 100;
