select bit_or_ints(prov_s) as prov_s,
bit_or_ints(prov_p) as prov_p,
 bit_or_ints(prov_ps1) as prov_ps1,
  bit_or_blob(prov_ps2) as prov_ps2  from (
select s_acctbal,s_name,n_name,p_partkey,p_mfgr,s_address,s_phone,s_comment, prov_s, prov_p, prov_ps1, prov_ps2
from (select p_partkey, p_type,p_size,p_mfgr, binsearch_ranges_pos(p_partkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(3125),(6250),(9375),(12500),(15625),(18750),(21875),(25000),(28125),(31250),(34375),(37500),(40625),(43750),(46875),(50000),(53125),(56250),(59375),(62500),(65625),(68750),(71875),(75000),(78125),(81250),(84375),(87500),(90625),(93750),(96875),(100000),(103125),(106250),(109375),(112500),(115625),(118750),(121875),(125000),(128125),(131250),(134375),(137500),(140625),(143750),(146875),(150000),(153125),(156250),(159375),(162500),(165625),(168750),(171875),(175000),(178125),(181250),(184375),(187500),(190625),(193750),(196875),(200001)) AS g(b))) AS prov_p from part) p1,
(select s_acctbal, s_name,s_suppkey, s_nationkey,s_address,s_phone,s_comment, binsearch_ranges_pos(s_suppkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(156),(312),(468),(624),(780),(936),(1092),(1248),(1404),(1560),(1716),(1872),(2028),(2184),(2340),(2496),(2652),(2808),(2964),(3120),(3276),(3432),(3588),(3744),(3900),(4056),(4212),(4368),(4524),(4680),(4836),(4992),(5149),(5305),(5462),(5618),(5775),(5931),(6088),(6244),(6401),(6557),(6714),(6870),(7027),(7183),(7340),(7496),(7653),(7809),(7966),(8122),(8279),(8435),(8592),(8748),(8905),(9061),(9218),(9374),(9531),(9687),(9844),(10001)) AS g(b))) AS prov_s from supplier) s1,
(select ps_partkey, ps_suppkey, ps_supplycost, binsearch_ranges_pos(ps_partkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(3125),(6250),(9375),(12500),(15625),(18750),(21875),(25000),(28125),(31250),(34375),(37500),(40625),(43750),(46875),(50000),(53125),(56250),(59375),(62500),(65625),(68750),(71875),(75000),(78125),(81250),(84375),(87500),(90625),(93750),(96875),(100000),(103125),(106250),(109375),(112500),(115625),(118750),(121875),(125000),(128125),(131250),(134375),(137500),(140625),(143750),(146875),(150000),(153125),(156250),(159375),(162500),(165625),(168750),(171875),(175000),(178125),(181250),(184375),(187500),(190625),(193750),(196875),(200001)) AS g(b))) AS prov_ps1 from partsupp) ps,nation,region,
(select min(ps_supplycost) as min_supplycost, bit_or_ints(prov_ps2) as prov_ps2, ps_partkey from (select ps_partkey, ps_suppkey, ps_supplycost, binsearch_ranges_pos(ps_partkey, (SELECT bounds_to_blob(b) FROM (VALUES (1),(3125),(6250),(9375),(12500),(15625),(18750),(21875),(25000),(28125),(31250),(34375),(37500),(40625),(43750),(46875),(50000),(53125),(56250),(59375),(62500),(65625),(68750),(71875),(75000),(78125),(81250),(84375),(87500),(90625),(93750),(96875),(100000),(103125),(106250),(109375),(112500),(115625),(118750),(121875),(125000),(128125),(131250),(134375),(137500),(140625),(143750),(146875),(150000),(153125),(156250),(159375),(162500),(165625),(168750),(171875),(175000),(178125),(181250),(184375),(187500),(190625),(193750),(196875),(200001)) AS g(b))) AS prov_ps2 from partsupp) ps2, supplier, nation, region
where s_suppkey = ps_suppkey and s_nationkey = n_nationkey and n_regionkey = r_regionkey and r_name = 'EUROPE' group by ps_partkey) m
where p_partkey = ps.ps_partkey and s_suppkey = ps.ps_suppkey and p_size = 15 and p_type like '%BRASS' and s_nationkey = n_nationkey and n_regionkey = r_regionkey and r_name = 'EUROPE'
and ps_supplycost = m.min_supplycost and p_partkey = m.ps_partkey
order by s_acctbal desc, n_name, s_name, p_partkey
LIMIT 100) bb;
